        var intervaltiming = setInterval(function(){
            var myclassname = ".four-element-carousel-counter"
            $(myclassname).each(function( i ) {                
                var getcounterid = "#" + $(myclassname).eq(i).attr("id");
                var getcarouselparent = $(getcounterid).attr('data-carousel-parent');
                var getcarousel = $(getcounterid).attr('data-carousel-target');
                var slidelength = $(getcarouselparent).find(getcarousel).find(".slick-slide").length;
                var currentslide = $(getcarouselparent + " " + ".slick-slide.slick-current").index() + 1;

                $(getcounterid).find(".current-item").text("0" + currentslide);
                $(getcounterid).find(".total-item").text("0" + slidelength);
            });

        }, 100);
        
 

        /*  TODO: fixed header top on scroll down
        @version 1.0.0
        @author [Prawesh Kumar] */
        $(window).on("scroll", function () {
        var lastScrollTop = '';
        function stickyMenu() {
            var st = $(window).scrollTop();  
            var mainMenuBottom = $('#top-header');
            if ($(window).scrollTop() > 10) {
                if (st > lastScrollTop) {
                    //to hide sticky menu on scroll down addClass replace to removeclass
                    mainMenuBottom.addClass("sticky-start");
                } else {
                    // active sticky menu on scroll up
                    mainMenuBottom.removeClass("sticky-start");
                }
    
            }else {
                mainMenuBottom.removeClass("sticky-start");
            }
            if ($(window).scrollTop() > 100) {
                if (st > lastScrollTop) {
                    //to hide sticky menu on scroll down addClass replace to removeclass
                    mainMenuBottom.addClass("shadow sticky-on");
                    // $('#scroll-to-top').removeClass("hide");
                } else {
                    // active sticky menu on scroll up
                    mainMenuBottom.removeClass("shadow sticky-on");
                    // $('#scroll-to-top').addClass("hide");
                }
    
            } else {
                mainMenuBottom.removeClass("shadow sticky-on");
                // $('#scroll-to-top').addClass("hide");
            }
    
            lastScrollTop = st;
        }
        stickyMenu();
    
        });   
    
    
